import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Inicio.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarDays, faBell, faArrowRight, faPlus, faCreditCard } from '@fortawesome/free-solid-svg-icons';
import { format, startOfMonth, endOfMonth, isBefore, formatDistanceToNow, startOfToday } from 'date-fns';
import ptBR from 'date-fns/locale/pt-BR';

const API_BASE_URL = 'http://localhost:8000'; 

function Inicio() {
    // Estados
    const [consultasHoje, setConsultasHoje] = useState([]);
    const [pagamentosResumo, setPagamentosResumo] = useState({ totalPrevisto: 0, pendente: 0, vencido: 0 });
    const [notificacoesRecentes, setNotificacoesRecentes] = useState([]);
    const [loadingConsultas, setLoadingConsultas] = useState(true);
    const [loadingPagamentos, setLoadingPagamentos] = useState(true);
    const [loadingNotificacoes, setLoadingNotificacoes] = useState(true);
    const [error, setError] = useState(null); 
    const navigate = useNavigate();

    //Funções de Busca
    // Busca pacientes e retorna um mapa ID -> Nome
    const fetchPacientesMap = useCallback(async () => {
        try {
            const token = localStorage.getItem('access_token');
            if (!token) throw new Error('Token não encontrado.');
            const response = await axios.get(`${API_BASE_URL}/api/pacientes/`, { headers: { 'Authorization': `Bearer ${token}` } });
            const pacientesData = response.data.results || response.data || [];
            const map = pacientesData.reduce((acc, paciente) => {
                acc[paciente.id_paciente] = paciente.nome;
                return acc;
            }, {});
            return map; 
        } catch (err) {
            console.error('Erro ao buscar pacientes:', err);
             if (err.response && err.response.status === 401) { navigate('/login'); }
             return {}; 
        }
    }, [navigate]);


    // Busca consultas do dia, usando o mapa de pacientes
    const fetchConsultasHoje = useCallback(async (pacMap) => { // Recebe o mapa
        setLoadingConsultas(true);
        setError(null);
        try {
            const token = localStorage.getItem('access_token');
            if (!token) throw new Error('Token não encontrado.');
            
            const hoje = format(new Date(), 'yyyy-MM-dd');
            // Busca consultas filtrando por data
            const response = await axios.get(`${API_BASE_URL}/api/agendamentos/?data_consulta=${hoje}`, { headers: { 'Authorization': `Bearer ${token}` } });
            const consultas = response.data.results || response.data || [];
            
            // Adiciona o nome do paciente usando o pacMap
            const consultasComNomes = consultas.map(consulta => ({
                 ...consulta,
                 nome_paciente: pacMap[consulta.paciente] || `ID: ${consulta.paciente}` 
            }));

            consultasComNomes.sort((a, b) => (a.horario_consulta || "").localeCompare(b.horario_consulta || ""));
            setConsultasHoje(consultasComNomes);
        } catch (err) {
            console.error('Erro ao buscar consultas de hoje:', err);
            setError('Falha ao carregar consultas.');
            if (err.response && err.response.status === 401) { navigate('/login'); }
        } finally {
            setLoadingConsultas(false);
        }
    }, [navigate]);

    // Busca parcelas e calcula resumo de pagamentos
    const fetchPagamentosResumo = useCallback(async () => {
        setLoadingPagamentos(true);
        setError(null);
        try {
            const token = localStorage.getItem('access_token');
            if (!token) throw new Error('Token não encontrado.');

            const hoje = new Date();
            const inicioMes = format(startOfMonth(hoje), 'yyyy-MM-dd');
            const fimMes = format(endOfMonth(hoje), 'yyyy-MM-dd');

            // Busca parcelas do mês (Pendente, Atrasado)
            const response = await axios.get(`${API_BASE_URL}/api/parcelas/?data_vencimento_after=${inicioMes}&data_vencimento_before=${fimMes}`, { headers: { 'Authorization': `Bearer ${token}` } });
            const parcelasMes = response.data.results || response.data || [];

            // Calcula totais
            let totalPrevisto = 0; let pendente = 0; let vencido = 0;
            const hojeParaComparar = startOfToday();

            parcelasMes.forEach(p => {
                const valor = parseFloat(p.valor_parcela || 0);
                    totalPrevisto += valor;

                if (p.status === 'Pendente' || p.status === 'Atrasado') {
                    pendente += valor;
                }
                const dataVencimento = new Date(p.data_vencimento + 'T00:00:00');
                if (p.status === 'Atrasado' || (p.status === 'Pendente' && isBefore(dataVencimento, hojeParaComparar))) {
                    vencido += valor;
                }
            });
            setPagamentosResumo({ totalPrevisto, pendente, vencido });

        } catch (err) {
            console.error('Erro ao buscar resumo de pagamentos:', err);
            setError('Falha ao carregar resumo financeiro.');
            if (err.response && err.response.status === 401) { navigate('/login'); }
        } finally {
            setLoadingPagamentos(false);
        }
    }, [navigate]);


    // Busca notificações recentes não lidas
    const fetchNotificacoesRecentes = useCallback(async () => {
        setLoadingNotificacoes(true);
        setError(null);
        try {
            const token = localStorage.getItem('access_token');
            if (!token) throw new Error('Token não encontrado.');
            const response = await axios.get(`${API_BASE_URL}/api/notificacoes/?lida=false&limit=4`, { headers: { 'Authorization': `Bearer ${token}` } });
            setNotificacoesRecentes(response.data.results || response.data || []);
        } catch (err) {
            console.error('Erro ao buscar notificações recentes:', err);
            if (err.response && err.response.status === 401) { navigate('/login'); }
        } finally {
            setLoadingNotificacoes(false);
        }
    }, [navigate]);


    // Efeito principal para buscar todos os dados
    useEffect(() => {
        const carregarDashboard = async () => {
            setLoadingConsultas(true); setLoadingPagamentos(true); setLoadingNotificacoes(true);
            const pacMap = await fetchPacientesMap(); // Busca o mapa primeiro
            // Dispara as outras buscas em paralelo, passando o mapa
            await Promise.all([
                fetchConsultasHoje(pacMap), // Passa o mapa
                fetchPagamentosResumo(),
                fetchNotificacoesRecentes()
            ]);
            // Loadings são controlados dentro de cada função fetch
        };
        carregarDashboard();
    }, [fetchPacientesMap, fetchConsultasHoje, fetchPagamentosResumo, fetchNotificacoesRecentes]); 


    // Função auxiliar para formatar tempo relativo
    const formatTempoAtras = (dateString) => {
         try {
             const date = new Date(dateString);
             return formatDistanceToNow(date, { addSuffix: true, locale: ptBR }); 
         } catch (e) { 
            console.error("Erro formatando data:", dateString, e);
            return dateString; 
         }
    };


    return (
        <div className="dashboard-container">
            {error && <p className="error-message" style={{textAlign: 'center', marginBottom: '1rem'}}>{error}</p>}

            <div className="dashboard-wrapper">
                {/* Linha de cima: Consultas e Pagamentos */}
                <div className="flex-row-container">

                    {/* Seção Consultas */}
                    <div className="dashboard-section consultations-section">
                        <div className="section-header">
                            <FontAwesomeIcon icon={faCalendarDays} />
                            <h2>Consultas do Dia</h2>
                        </div>
                        {loadingConsultas ? ( <p>Carregando consultas...</p> ) 
                         : consultasHoje.length > 0 ? (
                            <table className="dashboard-table">
                                <thead><tr><th>Horário</th><th>Paciente</th><th>Status</th></tr></thead>
                                <tbody>
                                    {consultasHoje.map(c => (
                                        <tr key={c.id_consulta}>
                                            <td>{c.horario_consulta?.substring(0, 5) || '-'}</td>
                                            <td>{c.nome_paciente || '-'}</td>
                                            <td>{c.status || 'N/A'}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : ( <p className="no-data-message">Nenhuma consulta agendada para hoje.</p> )}
                        <Link to="/agenda" className="dashboard-link-button">Ver agenda completa <FontAwesomeIcon icon={faArrowRight} /></Link>
                    </div>

                    {/* Seção Pagamentos */}
                    <div className="dashboard-section payments-section">
                        <div className="section-header">
                            <FontAwesomeIcon icon={faCreditCard} />
                            <h2>Pagamentos do Mês</h2>
                        </div>
                        {loadingPagamentos ? ( <p>Carregando resumo...</p> ) 
                         : ( <>
                                <div className="payment-item"><span>Total previsto:</span><strong>R$ {pagamentosResumo.totalPrevisto.toFixed(2)}</strong></div>
                                <div className="payment-item"><span>Pendentes:</span><strong>R$ {pagamentosResumo.pendente.toFixed(2)}</strong></div>
                                <div className="payment-item"><span>Vencido:</span><strong>R$ {pagamentosResumo.vencido.toFixed(2)}</strong></div>
                             </> )}
                        <Link to="/pagamentos" className="dashboard-link-button">Ver pagamentos <FontAwesomeIcon icon={faArrowRight} /></Link>
                    </div>
                </div>

                {/* Linha de baixo: Notificações e Acessos Rápidos */}
                <div className="flex-row-container">

                    {/* Seção Notificações */}
                    <div className="dashboard-section notifications-section">
                        <div className="section-header">
                            <FontAwesomeIcon icon={faBell} />
                            <h2>Notificações</h2>
                        </div>
                        {loadingNotificacoes ? ( <p>Carregando notificações...</p> ) 
                         : notificacoesRecentes.length > 0 ? (
                            <div className="notification-list-simple">
                                {notificacoesRecentes.map(notif => (
                                
                                <div key={notif.id_notificacao} className="notification-item-inicio">
                                    <p className="notification-message-inicio">{notif.mensagem}</p>
                                    <span className="notification-time-inicio">
                                    {formatTempoAtras(notif.data_criacao)}
                                    </span>
                                </div>

                                ))}
                            </div>

                            ) : (
                            <p className="no-data-message">Nenhuma notificação nova.</p>
                            )}
                            <Link to="/notificacoes" className="dashboard-link-button">
                                Ver todas as notificações <FontAwesomeIcon icon={faArrowRight} />
                            </Link>
                    </div>

                    {/* Seção Acessos Rápidos */}
                    <div className="dashboard-section acessos-rapidos-section">
                        <div className="acessos-rapidos-header"><h3>Acessos rápidos</h3></div>
                        <div className="quick-access-buttons">
                            <Link to="/patients/new" className="quick-access-button"><FontAwesomeIcon icon={faPlus} /> Novo paciente</Link>
                            {/* Ajuste o 'to' ou use 'state' para abrir modal na Agenda */}
                            <Link to="/agenda" state={{ openNewModal: true }} className="quick-access-button"><FontAwesomeIcon icon={faPlus} /> Nova consulta</Link>
                            {/* Ajuste o 'to' ou use 'state' para abrir modal em Orcamentos */}
                            <Link to="/orcamentos" state={{ openNewModal: true }} className="quick-access-button"><FontAwesomeIcon icon={faPlus} /> Novo orçamento</Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Inicio;
