import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './RegistroPacientes.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck, faPrint } from '@fortawesome/free-solid-svg-icons';
import { useReactToPrint } from 'react-to-print';
import AnamneseImpressao from '../componentes/impressao/AnamneseImpressao'; // Componente de impressão

const API_BASE_URL = 'http://localhost:8000';

//Breadcrumbs
const Breadcrumbs = ({ step }) => {
    const steps = ['Dados Pessoais', 'Anamnese 1', 'Anamnese 2', 'Concluir e Salvar'];
    const currentPath = steps.slice(0, step);
    return (
        <div className="breadcrumbs-container">
            {currentPath.map((name, index) => (
                <span key={index} className="breadcrumb-item">
                    {name}
                    {index < currentPath.length - 1 && <span className="separator">/</span>}
                </span>
            ))}
        </div>
    );
};

// DadosPessoais
const DadosPessoais = ({ onNext, data, onChange }) => {
    const [ufs, setUfs] = useState([]);
    const [cidades, setCidades] = useState([]);

    useEffect(() => { /* ... (fetchUfs) ... */
        const fetchUfs = async () => { try { const token = localStorage.getItem('access_token'); if (!token) return; const response = await axios.get(`${API_BASE_URL}/api/ufs/`, { headers: { 'Authorization': `Bearer ${token}` } }); const ufsData = response.data.results || response.data || []; setUfs(ufsData); } catch (error) { console.error('Erro ao buscar UFs:', error); } }; fetchUfs();
    }, []);

    useEffect(() => { /* ... (fetchCidades) ... */
        if (data.uf) { const fetchCidades = async () => { try { const token = localStorage.getItem('access_token'); if (!token) return; const response = await axios.get(`${API_BASE_URL}/api/cidades/by_uf/?uf_id=${data.uf}`, { headers: { 'Authorization': `Bearer ${token}` } }); setCidades(response.data || []); } catch (error) { console.error('Erro ao buscar Cidades:', error); setCidades([]); } }; fetchCidades(); } else { setCidades([]); }
    }, [data.uf]);

    return (
        <div className="dados-pessoais-container registro-tela">
            <h2 className="section-title">Dados Pessoais:</h2>
            <form onSubmit={(e) => { e.preventDefault(); onNext(); }}>
                <div className="form-group"><label htmlFor="nome">Nome: *</label><input type="text" id="nome" name="nome" value={data.nome || ''} onChange={onChange} className="input-field" required /></div>
                <div className="form-group-inline">
                    <div className="form-group"><label htmlFor="cpf">CPF: *</label><input type="text" id="cpf" name="cpf" value={data.cpf || ''} onChange={onChange} className="input-field" required /></div>
                    <div className="form-group"><label htmlFor="data_nasc">Data de Nascimento: *</label><input type="date" id="data_nasc" name="data_nasc" value={data.data_nasc || ''} onChange={onChange} className="input-field" required /></div>
                </div>
                 <div className="form-group"><label htmlFor="telefone">Telefone:</label><input type="tel" id="telefone" name="telefone" value={data.telefone || ''} onChange={onChange} className="input-field" /></div>
                <div className="form-group-inline">
                    <div className="form-group"><label htmlFor="uf">UF: *</label><select name="uf" id="uf" value={data.uf || ''} onChange={onChange} className="input-field" required><option value="">Selecione o Estado</option>{ufs.map(uf => ( <option key={uf.id_uf} value={uf.id_uf}>{uf.sigla_uf}</option> ))}</select></div>
                    <div className="form-group"><label htmlFor="cidade">Cidade: *</label><select name="cidade" id="cidade" value={data.cidade || ''} onChange={onChange} className="input-field" required disabled={!data.uf || cidades.length === 0}><option value="">Selecione a Cidade</option>{cidades.map(cidade => ( <option key={cidade.id_cidade} value={cidade.id_cidade}>{cidade.nome_cidade}</option> ))}</select></div>
                </div>
                <div className="form-group"><label htmlFor="logradouro">Endereço (Rua, Nº):</label><input type="text" id="logradouro" name="logradouro" value={data.logradouro || ''} onChange={onChange} className="input-field" /></div>
                <div className="form-group"><label htmlFor="email">Email:</label><input type="email" id="email" name="email" value={data.email || ''} onChange={onChange} className="input-field" /></div>
                <div className="form-actions-full"><button type="submit" className="button-primary">Próximo</button></div>
            </form>
        </div>
    );
};

// Anamnese1
const Anamnese1 = ({ onNext, onBack, data, onChange }) => {
    const handleValidateAndNext = (e) => { /* ... (validação) ... */
         e.preventDefault();
        if (data.trat_medico && !data.trat_med_desc?.trim()) { alert("Por favor, descreva o tratamento médico."); return; }
        if (data.medicamento && !data.medicamento_desc?.trim()) { alert("Por favor, descreva o medicamento utilizado."); return; }
        if (data.sensi_dentes && !data.sensi_dentes_desc?.trim()) { alert("Por favor, descreva a sensibilidade nos dentes."); return; }
        onNext();
    };
    return (
        <div className="registro-tela">
            <h2 className="section-title">Registro de Anamnese (1/2):</h2>
            <form onSubmit={handleValidateAndNext}>
                 <div className="form-group-textarea"><label htmlFor="motivo_consulta">Motivo da consulta:</label><textarea id="motivo_consulta" name="motivo_consulta" value={data.motivo_consulta || ''} onChange={onChange} rows="2" className="input-field" /></div>
                 <div className="form-group-textarea"><label htmlFor="ultimo_trat">Último Tratamento Odontológico:</label><textarea id="ultimo_trat" name="ultimo_trat" value={data.ultimo_trat || ''} onChange={onChange} rows="2" className="input-field" /></div>
                <div className="form-checkbox-group">
                     <label className="checkbox-label"><input type="checkbox" name="trat_medico" checked={data.trat_medico || false} onChange={onChange} /> Em tratamento médico? * {data.trat_medico && <input type="text" name="trat_med_desc" value={data.trat_med_desc || ''} onChange={onChange} className="input-conditional" placeholder="Qual?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="medicamento" checked={data.medicamento || false} onChange={onChange} /> Faz uso de medicamento? * {data.medicamento && <input type="text" name="medicamento_desc" value={data.medicamento_desc || ''} onChange={onChange} className="input-conditional" placeholder="Qual(is)?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="reacao_anestesia" checked={data.reacao_anestesia || false} onChange={onChange} /> Teve reação à anestesia? {data.reacao_anestesia && <input type="text" name="reacao_anest_desc" value={data.reacao_anest_desc || ''} onChange={onChange} className="input-conditional" placeholder="Qual? (Opcional)" />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="sensi_dentes" checked={data.sensi_dentes || false} onChange={onChange} /> Tem sensibilidade nos dentes?  {data.sensi_dentes && <input type="text" name="sensi_dentes_desc" value={data.sensi_dentes_desc || ''} onChange={onChange} className="input-conditional" placeholder="Com quê? (Frio, quente, doce)" required />}</label>
                </div>
                <div className="form-actions"><button type="button" className="button-secondary" onClick={onBack}>Voltar</button><button type="submit" className="button-primary">Próximo</button></div>
            </form>
        </div>
    );
};

//Anamnese2 
const Anamnese2 = ({ onNext, onBack, data, onChange }) => {
    const handleValidateAndNext = (e) => { /* ... (validação) ... */
        e.preventDefault();
        if (data.range_apertamento && !data.range_apert_desc?.trim()) { alert("Por favor, forneça detalhes sobre o rangimento/apertamento."); return; }
        if (data.fuma && !data.fuma_desc?.trim()) { alert("Por favor, forneça detalhes sobre o hábito de fumar."); return; }
        if (data.diabetico && !data.diabetico_desc?.trim()) { alert("Por favor, forneça detalhes sobre a diabetes."); return; }
        if (data.corte_sangra && !data.corte_sang_desc?.trim()) { alert("Por favor, forneça detalhes sobre o sangramento."); return; }
        if (data.prob_cardiaco && !data.prob_card_desc?.trim()) { alert("Por favor, descreva o problema cardíaco."); return; }
        if (data.dores_cabeca && !data.dores_cabeca_desc?.trim()) { alert("Por favor, descreva as dores de cabeça."); return; }
        if (data.desmaio_ataques && !data.desmaio_atq_desc?.trim()) { alert("Por favor, descreva os desmaios ou ataques."); return; }
        if (data.pressao_art && !data.pressao_art_desc?.trim()) { alert("Por favor, descreva sobre a pressão arterial."); return; }
        if (data.gengiva_sangra && !data.gengiva_sang_desc?.trim()) { alert("Por favor, descreva sobre o sangramento da gengiva."); return; }
        onNext();
    };
    return ( /* ... (JSX da Anamnese 2) ... */
        <div className="registro-tela">
            <h2 className="section-title">Registro de Anamnese (2/2):</h2>
            <form onSubmit={handleValidateAndNext}>
                <div className="form-checkbox-group">
                     <label className="checkbox-label"><input type="checkbox" name="range_apertamento" checked={data.range_apertamento || false} onChange={onChange} /> Range os dentes/apertamento?  {data.range_apertamento && <input type="text" name="range_apert_desc" value={data.range_apert_desc || ''} onChange={onChange} className="input-conditional" placeholder="Quando percebe?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="gengiva_sangra" checked={data.gengiva_sangra || false} onChange={onChange} /> Gengiva sangra facilmente?  {data.gengiva_sangra && <input type="text" name="gengiva_sang_desc" value={data.gengiva_sang_desc || ''} onChange={onChange} className="input-conditional" placeholder="Ao escovar, espontaneamente?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="fuma" checked={data.fuma || false} onChange={onChange} /> Fumante?  {data.fuma && <input type="text" name="fuma_desc" value={data.fuma_desc || ''} onChange={onChange} className="input-conditional" placeholder="Há quantos anos? Quantidade? Parou?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="diabetico" checked={data.diabetico || false} onChange={onChange} /> Diabético?  {data.diabetico && <input type="text" name="diabetico_desc" value={data.diabetico_desc || ''} onChange={onChange} className="input-conditional" placeholder="Tipo? Controlada?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="corte_sangra" checked={data.corte_sangra || false} onChange={onChange} /> Quando se corta, sangra muito?  {data.corte_sangra && <input type="text" name="corte_sang_desc" value={data.corte_sang_desc || ''} onChange={onChange} className="input-conditional" placeholder="Quanto tempo?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="prob_cardiaco" checked={data.prob_cardiaco || false} onChange={onChange} /> Tem problema cardíaco?  {data.prob_cardiaco && <input type="text" name="prob_card_desc" value={data.prob_card_desc || ''} onChange={onChange} className="input-conditional" placeholder="Qual?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="dores_cabeca" checked={data.dores_cabeca || false} onChange={onChange} /> Dores de cabeça?  {data.dores_cabeca && <input type="text" name="dores_cabeca_desc" value={data.dores_cabeca_desc || ''} onChange={onChange} className="input-conditional" placeholder="Frequência? Local?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="desmaio_ataques" checked={data.desmaio_ataques || false} onChange={onChange} /> Já teve desmaios ou ataques?  {data.desmaio_ataques && <input type="text" name="desmaio_atq_desc" value={data.desmaio_atq_desc || ''} onChange={onChange} className="input-conditional" placeholder="Quando?" required />}</label>
                     <label className="checkbox-label"><input type="checkbox" name="pressao_art" checked={data.pressao_art || false} onChange={onChange} /> Tem pressão arterial alterada?  {data.pressao_art && <input type="text" name="pressao_art_desc" value={data.pressao_art_desc || ''} onChange={onChange} className="input-conditional" placeholder="Alta ou baixa? Controlada?" required />}</label>
                </div>
                <div className="form-actions"><button type="button" className="button-secondary" onClick={onBack}>Voltar</button><button type="submit" className="button-primary">Próximo</button></div>
            </form>
        </div>
    );
};

//Concluido
const Concluido = ({ formData, onSave, onBack }) => {
    const componentRef = useRef(null);
    const [isSaving, setIsSaving] = useState(false);

    const handlePrint = useReactToPrint({
        content: () => componentRef.current,
    });

    useEffect(() => {
        console.log("Concluido useEffect: componentRef.current mudou para:", componentRef.current);
    }, [componentRef.current]);

    const handleTriggerSave = async () => {
        setIsSaving(true);
        const success = await onSave();
        setIsSaving(false);
        if (success) {
        }
    };

    const handlePrintClick = () => {
        console.log("No momento do clique: componentRef.current:", componentRef.current);
        if (componentRef.current) {
            handlePrint(); // Chama a função original do useReactToPrint
        } else {
            console.error("Referência do componente está nula no clique!");
            alert("Erro: Não foi possível encontrar o conteúdo para impressão.");
        }
    };

    return (
        <div className="registro-concluido registro-tela">
            <span className="icon-success"><FontAwesomeIcon icon={faCheck} /></span>
            <h2>Cadastro Concluído!</h2>
            <p>Os dados do paciente e a anamnese estão prontos para serem salvos.</p>

            <div style={{ display: 'none' }}>
                <div ref={componentRef} style={{ padding: '20px', border: '1px solid black' }}>
                    <h1>Teste de Impressão</h1>
                    <p>Se isto aparecer na impressão, o problema está no componente AnamneseImpressao.</p>
                    <p>Paciente: {formData?.nome || 'N/A'}</p>
                </div>
            </div>

            <button onClick={handlePrint} className="imprimir-button">
                <FontAwesomeIcon icon={faPrint} /> Imprimir Anamnese
            </button>

            <div className="form-actions">
                <button type="button" className="button-secondary" onClick={onBack} disabled={isSaving}>Voltar</button>
                <button type="button" className="button-primary" onClick={handleTriggerSave} disabled={isSaving}>
                    {isSaving ? 'Salvando...' : 'Salvar'}
                </button>
            </div>
        </div>
    );
};


//RegistroPacientes
function RegistroPacientes() {
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({});
    const navigate = useNavigate();

    const handleNext = () => setStep(prevStep => prevStep + 1);
    const handleBack = () => setStep(prevStep => prevStep - 1);
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        const finalValue = type === 'checkbox' ? checked : value;
        setFormData(prevData => ({ ...prevData, [name]: finalValue }));
    };

    const handleSave = async () => {
         console.log('handleSave iniciado. Dados atuais:', formData); // Log inicial
         try {
             const token = localStorage.getItem('access_token');
             if (!token) {
                 alert('Token de autenticação não encontrado. Faça login novamente.');
                 navigate('/'); 
                 return false; // Falha
             }
    
             //Prepara dados do Paciente
             const pacienteData = {
                 nome: formData.nome,
                 cpf: formData.cpf,
                 data_nasc: formData.data_nasc ? new Date(formData.data_nasc).toISOString().split('T')[0] : null, 
                 telefone: formData.telefone || null, // Envia null se vazio
                 logradouro: formData.logradouro || '', // Envia string vazia se vazio
                 email: formData.email || null, // Envia null se vazio
                 cidade: parseInt(formData.cidade), // ID da cidade
             };
             console.log('Enviando Paciente:', pacienteData); // Log dos dados do paciente

             if (!pacienteData.nome || !pacienteData.cpf || !pacienteData.data_nasc || !pacienteData.cidade) {
                 alert("Erro: Dados Pessoais obrigatórios (Nome, CPF, Data Nasc, Cidade) estão faltando.");
                 return false;
             }
    
             //Salva o Paciente PRIMEIRO
             let pacienteResponse;
             try {
                pacienteResponse = await axios.post(`${API_BASE_URL}/api/pacientes/`, pacienteData, { 
                    headers: { 'Authorization': `Bearer ${token}` } 
                });
                console.log("Paciente salvo com sucesso:", pacienteResponse.data); 
             } catch (pacienteError) {
                 const errorMsg = pacienteError.response?.data ? JSON.stringify(pacienteError.response.data) : pacienteError.message;
                 alert(`Erro CRÍTICO ao salvar dados do paciente: ${errorMsg}\nVerifique o console para mais detalhes.`);
                 console.error('Erro ao salvar paciente:', pacienteError.response || pacienteError);
                 return false; // Falha - Interrompe o processo
             }
             
             const novoPacienteId = pacienteResponse.data.id_paciente; // ID do paciente criado
             console.log("Paciente salvo com ID:", novoPacienteId);

             // Prepara dados da Anamnese
             // Garante que todos os campos booleanos tenham um valor (True/False)
             const anamneseData = {
                 motivo_consulta: formData.motivo_consulta || '',
                 ultimo_trat: formData.ultimo_trat || '',
                 trat_medico: formData.trat_medico || false,
                 trat_med_desc: formData.trat_medico ? (formData.trat_med_desc || null) : null, 
                 medicamento: formData.medicamento || false, // Garante False se undefined
                 medicamento_desc: formData.medicamento ? (formData.medicamento_desc || null) : null,
                 reacao_anestesia: formData.reacao_anestesia || false,
                 reacao_anest_desc: formData.reacao_anestesia ? (formData.reacao_anest_desc || null) : null,
                 sensi_dentes: formData.sensi_dentes || false,
                 sensi_dentes_desc: formData.sensi_dentes ? (formData.sensi_dentes_desc || null) : null,
                 range_apertamento: formData.range_apertamento || false,
                 range_apert_desc: formData.range_apertamento ? (formData.range_apert_desc || null) : null,
                 gengiva_sangra: formData.gengiva_sangra || false,
                 gengiva_sang_desc: formData.gengiva_sangra ? (formData.gengiva_sang_desc || null) : null,
                 fuma: formData.fuma || false,
                 fuma_desc: formData.fuma ? (formData.fuma_desc || null) : null, 
                 diabetico: formData.diabetico || false,
                 diabetico_desc: formData.diabetico ? (formData.diabetico_desc || null) : null,
                 corte_sangra: formData.corte_sangra || false,
                 corte_sang_desc: formData.corte_sangra ? (formData.corte_sang_desc || null) : null,
                 prob_cardiaco: formData.prob_cardiaco || false,
                 prob_card_desc: formData.prob_cardiaco ? (formData.prob_card_desc || null) : null,
                 dores_cabeca: formData.dores_cabeca || false,
                 dores_cabeca_desc: formData.dores_cabeca ? (formData.dores_cabeca_desc || null) : null,
                 desmaio_ataques: formData.desmaio_ataques || false,
                 desmaio_atq_desc: formData.desmaio_ataques ? (formData.desmaio_atq_desc || null) : null,
                 pressao_art: formData.pressao_art || false,
                 pressao_art_desc: formData.pressao_art ? (formData.pressao_art_desc || null) : null,
                 paciente: novoPacienteId,
                 data_anamnese: formData.data_anamnese || new Date().toISOString().split('T')[0],
                
             };
             console.log('Enviando Anamnese:', anamneseData); // Log dos dados da anamnese

             // Salva a Anamnese DEPOIS
             try {
                await axios.post(`${API_BASE_URL}/api/anamneses/`, anamneseData, { 
                    headers: { 'Authorization': `Bearer ${token}` } 
                });
                console.log("Anamnese salva com sucesso.");
             } catch (anamneseError) {
                  const errorMsg = anamneseError.response?.data ? JSON.stringify(anamneseError.response.data) : anamneseError.message;
                 alert(`Erro ao salvar dados da anamnese: ${errorMsg}\nOs dados do paciente foram salvos, mas a anamnese falhou. Verifique o console.`);
                 console.error('Erro ao salvar anamnese:', anamneseError.response || anamneseError);
                 return false; // Falha
             }
             return true;

         } catch (err) { // Erro geral
             if (err.response && err.response.status === 401) { 
                 alert('Sua sessão expirou. Faça login novamente.'); 
                 navigate('/'); 
             } else { 
                 alert(`Erro inesperado durante o salvamento: ${err.message}`); 
                 console.error('Erro geral em handleSave:', err); 
             }
             return false; // Falha
         }
     };

    const renderStep = () => {
        switch (step) {
            case 1: return <DadosPessoais onNext={handleNext} data={formData} onChange={handleInputChange} />;
            case 2: return <Anamnese1 onNext={handleNext} onBack={handleBack} data={formData} onChange={handleInputChange} />;
            case 3: return <Anamnese2 onNext={handleNext} onBack={handleBack} data={formData} onChange={handleInputChange} />;
            case 4: return <Concluido formData={formData} onSave={handleSave} onBack={handleBack} />;
            default: return <DadosPessoais onNext={handleNext} data={formData} onChange={handleInputChange} />;
        }
    };

    return (
        <div className="pacientes-container"><Breadcrumbs step={step} />{renderStep()}</div>
    );
}

export default RegistroPacientes;
