import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ListaOrcamentos.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEye, faQuestionCircle } from '@fortawesome/free-solid-svg-icons';
import Modal from '../componentes/Modal';
import { format } from 'date-fns';

const API_BASE_URL = 'http://localhost:8000';

function ListaOrcamentos() {
  const [orcamentos, setOrcamentos] = useState([]);
  const [pacientes, setPacientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Estados dos Filtros
  const [filtroPaciente, setFiltroPaciente] = useState('');
  const [filtroStatus, setFiltroStatus] = useState('');

  // Estado do Modal de Ajuda
  const [isAjudaOpen, setIsAjudaOpen] = useState(false);

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const dateOnly = dateString.split('T')[0];
    const [year, month, day] = dateOnly.split('-');
    return `${day}/${month}/${year}`;
  };

  // Busca Pacientes (para o filtro)
  const fetchPacientes = useCallback(async (token) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/pacientes/`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setPacientes(response.data.results || response.data || []);
    } catch (err) {
      console.error('Erro ao buscar pacientes:', err);
    }
  }, []);

  // Busca Orçamentos (com filtros)
  const fetchOrcamentos = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem('access_token');
      if (!token) throw new Error('Token de autenticação não encontrado.');

      let params = {};
      if (filtroPaciente) {
        params.paciente = filtroPaciente;
      }
      const response = await axios.get(`${API_BASE_URL}/api/orcamentos/`, {
        headers: { 'Authorization': `Bearer ${token}` },
        params: params
      });
      
      const orcamentosData = response.data.results || response.data || [];
      // Filtra por status no frontend (temporário)
      const orcamentosFiltrados = orcamentosData.filter(o => {
        if (!filtroStatus) return true;
        return o.status_tratamento === filtroStatus;
      });

      setOrcamentos(orcamentosFiltrados);
    } catch (err) {
      if (err.response && err.response.status === 401) {
        alert('Sua sessão expirou.');
        navigate('/login');
      } else {
        setError('Não foi possível carregar os orçamentos.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate, filtroPaciente, filtroStatus]);

  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (token) {
      fetchPacientes(token);
    }
  }, [fetchPacientes]);

  useEffect(() => {
    fetchOrcamentos();
  }, [fetchOrcamentos]);
  
  // Funções de Ação (Aprovar, Iniciar, Encerrar)

  const handleAprovarOrcamento = async (orcamentoId) => {
    const token = localStorage.getItem('access_token');
    const hoje = new Date().toISOString().split('T')[0];
    try {
      await axios.patch(`${API_BASE_URL}/api/orcamentos/${orcamentoId}/`, 
        { data_aprovacao: hoje },
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      alert('Orçamento aprovado com sucesso!');
      fetchOrcamentos(); // Recarrega a lista
    } catch (err) {
      alert("Erro ao aprovar o orçamento.");
    }
  };

  const handleIniciarTratamento = async (orcamentoId) => {
    const token = localStorage.getItem('access_token');
    try {
      await axios.post(`${API_BASE_URL}/api/orcamentos/${orcamentoId}/iniciar-tratamento/`, 
        {},
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      alert('Tratamento iniciado com sucesso!');
      fetchOrcamentos(); // Recarrega a lista
    } catch (err) {
      alert(`Erro ao iniciar o tratamento: ${err.response?.data?.error || err.message}`);
    }
  };

  const handleEncerrarTratamento = async (tratamentoId) => {
    if (!tratamentoId) { alert("Erro: ID do tratamento não encontrado."); return; }
    const token = localStorage.getItem('access_token');
    try {
      await axios.post(`${API_BASE_URL}/api/tratamentos/${tratamentoId}/encerrar/`, 
        {},
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      alert('Tratamento encerrado com sucesso!');
      fetchOrcamentos(); // Recarrega a lista
    } catch (err) {
      alert(`Erro ao encerrar o tratamento: ${err.response?.data?.error || err.message}`);
    }
  };

  return (
    <div className="orcamentos-lista-container">
      
      <div className="orcamentos-header">
        {/* Este H2 é opcional, já que o título está no Topo.js */}
        {/* <h2>Gerenciador de Orçamentos</h2> */}
        
        {/* Deixamos um espaço vazio para o botão "Novo" ficar à direita */}
        <div /> 
        
        <div className="orcamentos-header-actions">
          <Link 
            to="/orcamentos" 
            className="novo-orcamento-button"
            title="Ir para a tela de criação de um novo orçamento."
          >
            <FontAwesomeIcon icon={faPlus} /> Novo Orçamento
          </Link>
        </div>
      </div>

      {/* Filtros */}
      <div className="filtros-container">
        <div className="filtro-grupo">
          <label htmlFor="filtro-paciente">Filtrar por Paciente:</label>
          <select 
            id="filtro-paciente" 
            className="filtro-select" 
            value={filtroPaciente}
            onChange={(e) => setFiltroPaciente(e.target.value)}
            title="Exibir orçamentos apenas deste paciente."
          >
            <option value="">Todos os Pacientes</option>
            {pacientes.map(p => (
              <option key={p.id_paciente} value={p.id_paciente}>{p.nome}</option>
            ))}
          </select>
        </div>
        <div className="filtro-grupo">
          <label htmlFor="filtro-status">Filtrar por Status:</label>
          <select 
            id="filtro-status" 
            className="filtro-select"
            value={filtroStatus}
            onChange={(e) => setFiltroStatus(e.target.value)}
            title="Exibir orçamentos com o status selecionado."
          >
            <option value="">Todos os Status</option>
            <option value="Aguardando Aprovação">Aguardando Aprovação</option>
            <option value="Aprovado">Aprovado</option>
            <option value="Iniciado">Iniciado</option>
            <option value="Concluído">Concluído</option>
          </select>
        </div>
      </div>
      
      {error && <p className="error-message">{error}</p>}
      
      {/* Tabela de Orçamentos */}
      <div className="orcamentos-tabela-wrapper">
        <table className="orcamentos-tabela">
          <thead>
            <tr>
              <th>ID</th>
              <th>Paciente</th>
              <th>Data</th>
              <th>Valor Total (R$)</th>
              <th>Status</th>
              <th className="actions-header-cell">Ações</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6" className="loading-message">Carregando orçamentos...</td></tr>
            ) : orcamentos.length > 0 ? (
              orcamentos.map((orc) => (
                <tr key={orc.id_orcamento}>
                  <td>{orc.id_orcamento}</td>
                  <td>
                    <Link 
                      to={`/patients/${orc.paciente}`} 
                      className="paciente-link"
                      title="Ver os detalhes completos deste paciente."
                    >
                      {pacientes.find(p => p.id_paciente === orc.paciente)?.nome || 'Carregando...'}
                    </Link>
                  </td>
                  <td>{formatDate(orc.data_orcamento)}</td>
                  <td>{parseFloat(orc.valor_total || 0).toFixed(2)}</td>
                  <td>
                    <span 
                      style={{fontWeight: 'bold', color: orc.data_aprovacao ? 'green' : '#a88532'}}
                      title={`Status atual: ${orc.status_tratamento}`}
                    >
                      {orc.status_tratamento}
                    </span>
                  </td>
                  <td className="actions-cell">
                    <button 
                      className="action-button"
                      title="Ir para a tela de Detalhes do Paciente (onde você pode ver os itens)."
                      onClick={() => navigate(`/patients/${orc.paciente}`)}
                    >
                      <FontAwesomeIcon icon={faEye} />
                    </button>
                    
                    {!orc.data_aprovacao && orc.status_tratamento === "Aguardando Aprovação" && (
                      <button 
                        className="action-button action-button-aprovar"
                        title="Marcar este orçamento como aprovado."
                        onClick={() => handleAprovarOrcamento(orc.id_orcamento)}
                      >
                        Aprovar
                      </button>
                    )}

                    {orc.data_aprovacao && orc.status_tratamento === "Aprovado" && (
                      <button 
                        className="action-button action-button-iniciar"
                        title="Iniciar o tratamento deste orçamento."
                        onClick={() => handleIniciarTratamento(orc.id_orcamento)}
                      >
                        Iniciar
                      </button>
                    )}

                    {orc.status_tratamento === "Iniciado" && orc.tratamento_id && (
                      <button 
                        className="action-button action-button-encerrar"
                        title="Marcar este tratamento como concluído."
                        onClick={() => handleEncerrarTratamento(orc.tratamento_id)}
                      >
                        Encerrar
                      </button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr><td colSpan="6" className="no-data">Nenhum orçamento encontrado.</td></tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Ajuda Flutuante */}
      <button 
        className="floating-help-button" 
        onClick={() => setIsAjudaOpen(true)}
        title="Ajuda sobre esta página"
      >
        <FontAwesomeIcon icon={faQuestionCircle} />
      </button>

      <Modal
        isOpen={isAjudaOpen}
        onClose={() => setIsAjudaOpen(false)}
        titulo="Ajuda - Gerenciador de Orçamentos"
        footer={
          <button 
            className="modal-button modal-button-secondary" 
            onClick={() => setIsAjudaOpen(false)}
            title="Fechar esta janela."
          >
            Fechar
          </button>
        }
      >
        <p>Esta tela centraliza todos os orçamentos do sistema para gerenciamento.</p>
        <ul style={{ paddingLeft: '20px', lineHeight: '1.6' }}>
          <li>
            <strong>Visão Geral:</strong> A tabela lista todos os orçamentos criados.
          </li>
          <li>
            <strong>Filtros:</strong> Você pode filtrar a lista por Paciente ou por Status (Aguardando, Aprovado, etc.).
          </li>
          <li>
            <strong>Novo Orçamento:</strong> O botão no topo leva à tela de criação de um novo orçamento.
          </li>
          <li>
            <strong>Ações:</strong>
            <ul>
              <li>(Ícone do Olho): Visualiza os detalhes do paciente (onde você pode ver os itens do orçamento).</li>
              <li><strong>Aprovar:</strong> Confirma o orçamento e gera as parcelas financeiras.</li>
              <li><strong>Iniciar:</strong> Inicia o tratamento (disponível após aprovação).</li>
              <li><strong>Encerrar:</strong> Finaliza um tratamento em andamento.</li>
            </ul>
          </li>
        </ul>
      </Modal>

    </div>
  );
}

export default ListaOrcamentos;
