// src/context/NotificacaoContext.js
import React, { createContext, useState, useContext, useCallback, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const API_BASE_URL = 'http://localhost:8000'; 

const NotificacaoContext = createContext();

export const useNotificacoes = () => useContext(NotificacaoContext);

export const NotificacaoProvider = ({ children }) => {
    // console.log('--- NotificacaoProvider Montado ---'); // Log (pode remover depois)
    const [unreadCount, setUnreadCount] = useState(0);
    const [isLoadingCount, setIsLoadingCount] = useState(true);
    const navigate = useNavigate(); 

    const fetchUnreadCount = useCallback(async () => {
        // console.log('1. fetchUnreadCount chamado.'); 
        // setIsLoadingCount(true); // Removido daqui para evitar piscar
        const token = localStorage.getItem('access_token');
        // console.log('2. Token encontrado:', token ? 'Sim' : 'Não'); 

        if (!token) {
            setUnreadCount(0);
            setIsLoadingCount(false);
            return;
        }

        try {
            // console.log('3. Tentando buscar notificações não lidas...'); 
            const response = await axios.get(`${API_BASE_URL}/api/notificacoes/?lida=false`, { 
                headers: { 'Authorization': `Bearer ${token}` }
            });
            // console.log('4. Resposta da API:', response.data); 

            // Usa length do array ou count do objeto paginado
            if (Array.isArray(response.data)) {
                 setUnreadCount(response.data.length); 
                 // console.log(`Contagem definida como: ${response.data.length}`);
            } else if (response.data && typeof response.data.count === 'number') {
                 setUnreadCount(response.data.count);
                 // console.log(`Contagem definida como (via count): ${response.data.count}`);
            } else {
                 setUnreadCount(0); 
                 // console.warn("Resposta da API de notificações não reconhecida...");
            }

        } catch (err) {
            console.error('Erro detalhado em fetchUnreadCount:', err); 
             if (err.response && err.response.status === 401) {
                 localStorage.removeItem('access_token'); localStorage.removeItem('refresh_token'); localStorage.removeItem('user_id'); setUnreadCount(0); navigate('/login', { replace: true }); 
             }
             // Em caso de outros erros, mantém a contagem anterior ou 0
             // setUnreadCount(0); // Opcional: resetar em qualquer erro?
        } finally {
            // console.log('5. Finalizando busca, isLoadingCount = false'); 
            setIsLoadingCount(false);
        }
    }, [navigate]);

    // Busca inicial
    useEffect(() => {
        // console.log('--- useEffect do Provider disparado ---'); 
        fetchUnreadCount();
    }, [fetchUnreadCount]); 

    // --- DEFINIÇÃO CORRETA de decrementUnreadCount ---
    const decrementUnreadCount = useCallback(() => {
        setUnreadCount(prevCount => (prevCount > 0 ? prevCount - 1 : 0));
    }, []); // Dependência vazia, a função não muda

    // --- DEFINIÇÃO CORRETA do objeto value ---
    const value = {
        unreadCount,        // <-- Inclui a contagem
        isLoadingCount,     // <-- Inclui o status de loading
        fetchUnreadCount,   // <-- Inclui a função para re-buscar
        decrementUnreadCount // <-- Inclui a função para decrementar
    };
    // ------------------------------------------

    // Log opcional para ver o valor fornecido
    // console.log('NotificacaoContext: Fornecendo o valor:', value);

    return (
        <NotificacaoContext.Provider value={value}>
            {children}
        </NotificacaoContext.Provider>
    );
};
