// src/paginas/ListaPacientes.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ListaPacientes.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faPlus, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

const API_BASE_URL = 'http://localhost:8000';

function ListaPacientes() {
    const [pacientes, setPacientes] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    // Função para formatar data (opcional, mas melhora a exibição)
    const formatDate = (dateString) => {
        if (!dateString) return '-';
        // Remove a parte da hora (ex: T00:00:00)
        const dateOnly = dateString.split('T')[0];
        const [year, month, day] = dateOnly.split('-');
        return `${day}/${month}/${year}`;
    };

    useEffect(() => {
        const fetchPacientes = async () => {
            try {
                const token = localStorage.getItem('access_token');
                if (!token) {
                    throw new Error('Token de autenticação não encontrado.');
                }
                const response = await axios.get(`${API_BASE_URL}/api/pacientes/`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                const pacientesData = response.data.results || response.data;
                if (Array.isArray(pacientesData)) {
                    setPacientes(pacientesData);
                } else {
                    setPacientes([]);
                    setError('Formato de dados inesperado da API. Os dados não são uma lista.');
                }
            } catch (err) {
                if (err.response && err.response.status === 401) {
                    alert('Sua sessão expirou. Por favor, faça o login novamente.');
                    navigate('/login');
                } else {
                    setError('Não foi possível carregar os pacientes. Verifique sua conexão ou se o back-end está ativo.');
                    console.error('Erro ao buscar pacientes:', err);
                }
            } finally {
                setLoading(false);
            }
        };

        fetchPacientes();
    }, [navigate]);

    // Função para deletar paciente
    const handleDelete = async (pacienteId) => {
        if (window.confirm("Tem certeza que deseja excluir este paciente? Esta ação não pode ser desfeita.")) {
            try {
                const token = localStorage.getItem('access_token');
                await axios.delete(`${API_BASE_URL}/api/pacientes/${pacienteId}/`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                setPacientes(pacientes.filter(p => p.id_paciente !== pacienteId));
                alert("Paciente excluído com sucesso!");
            } catch (err) {
                console.error("Erro ao excluir paciente:", err);
                alert("Não foi possível excluir o paciente.");
            }
        }
    };

    const pacientesFiltrados = pacientes.filter(paciente => {
        const termo = searchTerm.toLowerCase();
        const nome = paciente.nome ? paciente.nome.toLowerCase() : '';
        const cpf = paciente.cpf ? paciente.cpf.toString() : '';
        return nome.includes(termo) || cpf.includes(termo);
    });

    if (loading) {
        return <div className="loading-message">Carregando pacientes...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="pacientes-container">
            <div className="pacientes-header">
                <div className="pacientes-header-actions">
                    <div className="search-bar">
                        <FontAwesomeIcon icon={faSearch} className="search-icon" />
                        <input
                            type="text"
                            placeholder="Buscar por nome ou CPF"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <Link to="/patients/new" className="novo-paciente-button">
                        <FontAwesomeIcon icon={faPlus} /> Novo paciente
                    </Link>
                </div>
            </div>

            <div className="pacientes-tabela-wrapper">
                <table className="pacientes-tabela">
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>CPF</th>
                            <th>Telefone</th>
                            {/* COLUNAS DE VOLTA */}
                            <th>Data de nascimento</th> 
                            <th>Endereço</th> 
                            {/* Coluna de ações com classe para estilização */}
                            <th className="actions-header-cell"></th> 
                        </tr>
                    </thead>
                    <tbody>
                        {pacientesFiltrados.length > 0 ? (
                            pacientesFiltrados.map(paciente => (
                                <tr key={paciente.id_paciente}>
                                    <td>
                                        <Link to={`/patients/${paciente.id_paciente}`} className="paciente-link">
                                            {paciente.nome}
                                        </Link>
                                    </td>
                                    <td>{paciente.cpf}</td>
                                    <td>{paciente.telefone}</td>
                                    {/* DADOS DE VOLTA (formatDate é a nova função) */}
                                    <td>{formatDate(paciente.data_nasc)}</td> 
                                    {/* DADOS DE VOLTA (usando 'logradouro' do seu models.py) */}
                                    <td>{paciente.logradouro || '-'}</td> 
                                    
                                    <td className="actions-cell">
                                        <button 
                                            onClick={() => navigate(`/patients/${paciente.id_paciente}/edit`)} 
                                            className="action-button edit-button"
                                            title="Editar"
                                        >
                                            <FontAwesomeIcon icon={faEdit} />
                                        </button>
                                        <button 
                                            onClick={() => handleDelete(paciente.id_paciente)} 
                                            className="action-button delete-button"
                                            title="Excluir"
                                        >
                                            <FontAwesomeIcon icon={faTrash} />
                                        </button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                {/* Atualizado colSpan para 6 colunas */}
                                <td colSpan="6" className="no-data">Nenhum paciente encontrado.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ListaPacientes;
