import React from 'react';
import './Modal.css'; // Importa nosso novo CSS

/**
 * Componente Modal Reutilizável
 * * Props:
 * - isOpen (boolean): Controla se o modal está visível.
 * - onClose (function): Função chamada ao clicar no 'X' ou no fundo.
 * - titulo (string): O texto do título no cabeçalho.
 * - children (ReactNode): O conteúdo principal do modal (o texto de ajuda/pergunta).
 * - footer (ReactNode): Opcional. Um contêiner para os botões.
 */
function Modal({ isOpen, onClose, titulo, children, footer }) {
  
  // Se não estiver aberto, não renderiza nada
  if (!isOpen) {
    return null;
  }

  // Impede que o clique no conteúdo feche o modal
  const handleContentClick = (e) => {
    e.stopPropagation();
  };

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-content" onClick={handleContentClick}>
        
        <div className="modal-header">
          <h3 className="modal-title">{titulo}</h3>
          <button onClick={onClose} className="modal-close-button" aria-label="Fechar">
            &times; {/* Este é um 'X' em HTML */}
          </button>
        </div>

        <div className="modal-body">
          {children}
        </div>

        {/* Renderiza o rodapé apenas se ele for fornecido */}
        {footer && (
          <div className="modal-footer">
            {footer}
          </div>
        )}

      </div>
    </div>
  );
}

export default Modal;
